import { FC, HTMLAttributes } from "react";
export interface Props extends HTMLAttributes<HTMLDivElement> {
    /**Step Number */
    step?: number;
    /** Top Label **/
    title?: string;
    /** Bottom Label**/
    subtitle?: string;
    /**Icon indicador de step */
    hasIcon?: boolean;
    /**Variante del icon */
    iconType?: 'regular' | 'small';
    /**Estado del step
     * Completed: paso finalizado (Done)
     * Active:    paso activo (Doing)
     * Disabled:  paso no clickeable, desactiva la acción asociada
     * Enabled:   paso no completo con acción habilitada
    */
    state?: 'completed' | 'active' | 'disabled' | 'enabled';
}
export declare const StepLabel: FC<Props>;
