import React, { FC, HTMLAttributes } from "react";
export interface Props extends HTMLAttributes<HTMLParagraphElement> {
    /** Tipo de texto*/
    type?: 'S1' | 'S2' | 'S3' | 'S4' | 'S5' | 'S7' | 'S8';
    /** Alineacion del texto*/
    textAlign?: 'left' | 'right' | 'center';
    /** Variante de estilo del text type */
    variant?: 'web' | 'mobile';
    /** Contenido del texto*/
    text?: string;
    /** Bold */
    bold?: boolean;
    /**Italic */
    italic?: boolean;
    /**Indica si debe usarse etiqueta </a> */
    isLink?: boolean;
    /**Deja abierto un children para nextLink (TODO)*/
    nextLink?: Node;
    /**Href en caso de ser  link */
    href?: string;
    /**overline */
    overline?: boolean;
    /**Color del texto */
    color?: string;
    dynamicClassName?: any;
    children?: React.ReactNode;
    isAction?: boolean;
}
export declare const Text: FC<Props>;
