import React, { FC, HTMLAttributes } from "react";
import { Text } from "../typography/Text";
import styles from './breadcrumb.module.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";
export interface Props extends HTMLAttributes<HTMLDivElement> {

    /** Setea la posici√≥n inicial*/
    list?: BreadcrumbElement[],


}
interface BreadcrumbElement {
    label: string,
    href: string
}

export const Breadcrumb: FC<Props> = ({ list = [] }: Props) => {


    return (
        <div className={styles.breadcrumbContainer}>
            {list.map((e,i) =>
                <>
                    <Text text={e.label} isLink={i==list.length-1?false:true} href={e.href} className={styles.progressBarLabel}></Text>
                    <FontAwesomeIcon style={{marginLeft:5,marginRight:5,height:15}}icon={faChevronRight}></FontAwesomeIcon>
                </>
            )}
        </div>
    )
}