
import { faEdit, IconDefinition } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { HTMLAttributes } from "react";
import { c } from "../../colors";
import styles from './buttonIcon.module.css'
export interface Props extends HTMLAttributes<HTMLButtonElement> {

    disabled?: boolean,
    listPosition?:"left"|"right",
    onClick():any,
    icon:IconDefinition

}

export const ButtonIcon = ({ onClick,disabled = false,icon=faEdit}: Props) => {



    return (
        <div className={styles.securityArea} style={{ width: "fit-content", position: "relative"}}>
            <div onClick={onClick} className={disabled ? styles.elipsisBoxDisabled : styles.elipsisBox}>
                  <FontAwesomeIcon
                  color={disabled?c({type:"grey-40"}):c({type:"black"})}
                  icon={icon}
                  fontSize={16}
                  ></FontAwesomeIcon>
              
            </div>
        
        </div>
    )
}