
import React, { HTMLAttributes } from "react";
import { useEffect } from "react";
import { useState } from "react";
import styles from './dropdown.module.css';
import { DropdownOption } from './DropdownOption';
interface Props extends HTMLAttributes<HTMLDivElement> {
    options: any,
    setSeleted:(e?: any)=> any
    disabled?: boolean,
    open?:boolean;

}

export const Dropdown = ({ options,open=false,setSeleted }: Props) => {


    const [isOpened,setIsOpened]=useState(false);

     useEffect(()=>{setIsOpened(open)
    },[open]);


    return (
        <div  style={{visibility:isOpened?"visible":"hidden"}} className={styles.dropdownContainer}>
            <div className={styles.dropdownInnerContainer} >

            {options.map((option: any) =>
                <DropdownOption setSelected={setSeleted} label={option.label} value={option.value}></DropdownOption>
            )
            }
            </div>
        </div>
    )
}