
import React, { HTMLAttributes } from "react";
import styles from './dropdownOption.module.css'
interface Props extends HTMLAttributes<HTMLDivElement> {
    label:string,
    disabled?: boolean,
    value?:string,
    setSelected(e?: any): any,
}

export const DropdownOption = ({label,setSelected}: Props) => {


 

    return (
        <div onClick={()=>setSelected(label)} className={styles.dropdownOptionContainer}>
        <p onClick={()=>console.log(label)} style={{margin:"0px",width:"100%"}}>{label}</p>
        </div>
    )
}