
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faInfo  } from "@fortawesome/free-solid-svg-icons";

import React, { HTMLAttributes, useState } from "react";
import styles from './tooltip.module.css'
import { Text } from "../typography/Text";
interface Props extends HTMLAttributes<HTMLButtonElement> {
    text: string,
    variant: 'light' | 'dark',
    disabled?: boolean
}

export const Tooltip = ({ text = "Prueba de texto de ejemplo en tooltip", variant = "light" }: Props) => {

    const [showTool,setShowTool]=useState(false);

    return (
        <div className={styles.securityArea} style={{ width: "fit-content", position: "relative"}}>
            <div  onMouseEnter={()=>setShowTool(true)} onMouseLeave={()=>setShowTool(false)} className={styles.infoIconContainer} >
                < FontAwesomeIcon
                  
                    className={styles.infoIcon} icon={faInfo}
                ></FontAwesomeIcon>
            </div>
            {showTool&&<div className={styles.tooltipTextContainer}>
                <div className={styles.securityArea}>
                    <div className={variant=="light"?styles.tooltipTextBoxLight:styles.tooltipTextBoxDark}>
                        <Text text={text} type={"S7"}></Text>
                    </div>
                </div>
            </div>}
        </div>
    )
}