

import React, { HTMLAttributes } from "react";
import styles from './iconState.module.css'
import { Text } from "../typography/Text";

export interface Props extends HTMLAttributes<HTMLButtonElement> {
label:string,
state: 'danger' | 'success' | 'warning' | 'disabled' | 'info'
}

export const IconState = ({label="Label",state='info'}: Props) => {


    const stateColor = {
        'danger': '#C51111',
        'success': '#258825',
        'warning': '#E99D09',
        'disabled': '#6A6A6A',
        'info': '#2970BC'
    }
    return (
        <div className={styles.iconStateContainer}> <div style={{ backgroundColor: stateColor[state] }} className={styles.stateCircle}></div><Text type="S7" color="#2B2B2B" text={label}></Text></div> )
}