import React, { FC, HTMLAttributes } from "react";
import { StepIcon } from "./StepIcon";
import styles from './stepLabel.module.css'

export interface Props extends HTMLAttributes<HTMLDivElement> {
    /**Step Number */
    step?:number,
    /** Top Label **/
    title?: string,
    /** Bottom Label**/
    subtitle?: string,
    /**Icon indicador de step */
    hasIcon?:boolean,

    /**Variante del icon */
    iconType?:'regular'|'small'

    /**Estado del step 
     * Completed: paso finalizado (Done)
     * Active:    paso activo (Doing)
     * Disabled:  paso no clickeable, desactiva la acción asociada
     * Enabled:   paso no completo con acción habilitada
    */
    state?:'completed'|'active'| 'disabled'|'enabled',

}

export const StepLabel: FC<Props> = ({ step=1,state='enabled',iconType='regular',hasIcon=false,title = "Title", subtitle = "SubTitle" }: Props) => {




    return (
        <div className={styles.stepLabelContainer}>
          {hasIcon&&<StepIcon step={step} state={state} type={iconType}></StepIcon>}
          <div className={styles.stepTitleContainer}>
          <div className={styles.stepTitle}><p>{title}</p></div>
          <div className={styles.stepSubtitle}><p>{subtitle}</p></div>
          </div>
        </div>
    )
}